//import '../SkinTemptColor.html'
export function SkinTemptColor(props){
    let preview = document.getElementById("preview");
    let recordingTimeMS = 5000;
    let recording = document.getElementById("recording");
    function handleClick(event){

        if (navigator.mediaDevices && navigator.mediaDevices.getUserMedia) {
            navigator.mediaDevices.getUserMedia({
                video: true
            })

            .then((stream) => {
                preview.srcObject = stream;

                preview.captureStream =
                preview.captureStream || preview.mozCaptureStream;
                return new Promise((resolve) => (preview.onplaying = resolve));
            })
            .then(() => startRecording(preview.captureStream(), recordingTimeMS))
            .then((recordedChunks) => {
                let recordedBlob = new Blob(recordedChunks, { type: "video/webm" });
                recording.src = URL.createObjectURL(recordedBlob);

            console.log(
                `Successfully recorded ${recordedBlob.size} bytes of ${recordedBlob.type} media.`
             );
            })
            .catch((error) => {
                if (error.name === "NotFoundError") {
                    console.log("Camera or microphone not found. Can't record.");
                } else {
                    console.log('error');
            }
            });


            function wait(delayInMS) {
                return new Promise((resolve) => setTimeout(resolve, delayInMS));
              }

              function stop(stream) {
                stream.getTracks().forEach((track) => track.stop());
              }


              function startRecording(stream, lengthInMS) {
                let recorder = new MediaRecorder(stream);
                let data = [];

                recorder.ondataavailable = (event) => data.push(event.data);
                recorder.start();
                console.log(`${recorder.state} for ${lengthInMS / 1000} seconds…`);

                let stopped = new Promise((resolve, reject) => {
                  recorder.onstop = resolve;
                  recorder.onerror = (event) => reject(event.name);
                });

                let recorded = wait(lengthInMS).then(() => {
                  if (recorder.state === "recording") {
                    recorder.stop();
                  }
                });

                return Promise.all([stopped, recorded]).then(() => data);
              }

              stopButton.addEventListener(
                "click",
                () => {
                  stop(preview.srcObject);
                },
                false
              );


        }

        return(
            <p>this function will help you to know your skin Tempterature</p>

        );

    }
    return (
        <div className="main-body">
                <button onClick={handleClick}> start testing</button>
                <p>this function will help you to know your skin Tempterature</p>
                <p>camera required</p>
                <video id="preview" width="160" height="120" autoplay muted></video>
                <button id="stopButton" class="button">Stop Recording</button>

        </div>

    );
}
